mapper.select:
select user_id, follow_id
from follows
where user_id = #{userId}
  and follow_id = #{followId}

mapper.delete:
delete
from follows
where user_id = #{userId}
  and follow_id = #{followId}

mapper.select:
select id, username, email, password, bio, image
from users
where email = #{email}

select id, username, email, password, bio, image
from users
where username = #{username}

select id, username, email, password, bio, image
from users
where id = #{id}

mapper.update:
update users
username = #{user.username}, email = #{user.email}, password = #{user.password}, bio = #{user.bio}, image = #{user.image}
where id = #{user.id}
